import React from "react";
import { useState } from "react";
import axios from "axios";

export default function QueryForm() {
  const [agent, setAgent] = useState("github");
  const [text, setText] = useState("summarize latest PRs");
  const [repo, setRepo] = useState("octocat/Hello-World");
  const [token, setToken] = useState("");
  const [response, setResponse] = useState(null);
  const [logs, setLogs] = useState([]);
  const [showLogs, setShowLogs] = useState(false);


  const loadLogs = async () => {
    try {
      const res = await axios.get("http://localhost:8000/mcp/logs");
      setLogs(res.data.logs);
      setShowLogs(true);
    } catch (err) {
      console.error("Failed to load logs", err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:8000/mcp/query", {
        agent,
        text,
        repo,
        token,
        limit: 5
      });
      setResponse(res.data.response);
    } catch (err) {
      setResponse(["Error: " + err.message]);
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-4 space-y-4">
      <h1 className="text-2xl font-bold">ðŸ§  AgentDock UI</h1>
      <form onSubmit={handleSubmit} className="space-y-2">
        <select value={agent} onChange={e => setAgent(e.target.value)} className="w-full border p-2">
          <option value="github">GitHubAgent</option>
        </select>
        <input className="w-full border p-2" placeholder="GitHub Repo (e.g. octocat/Hello-World)" value={repo} onChange={e => setRepo(e.target.value)} />
        <input className="w-full border p-2" placeholder="GitHub Token" type="password" value={token} onChange={e => setToken(e.target.value)} />
        <input className="w-full border p-2" placeholder="Ask something..." value={text} onChange={e => setText(e.target.value)} />
        <button className="bg-blue-600 text-white p-2 w-full rounded">Ask Agent</button>
      </form>
      <div className="bg-gray-100 p-4 rounded">
        <h2 className="font-semibold mb-2">Response</h2>
        <pre className="whitespace-pre-wrap">{response ? response.join("\n\n") : "No response yet."}</pre>
      </div>
    </div>
  );
}
